package hackerrank;

import static hackerrank.SubstringDiff.findLongestCommonSubstringWithAllowedDifferences;
import static org.junit.Assert.assertEquals;

import org.junit.Test;

public class SubstringDiffTest {

	@Test
	public void testSimpleCases() {
		assertEquals(
				4,
				findLongestCommonSubstringWithAllowedDifferences("tabriz",
						"torino", 2));
		assertEquals(
				3,
				findLongestCommonSubstringWithAllowedDifferences("abacba",
						"abcaba", 0));
		assertEquals(
				8,
				findLongestCommonSubstringWithAllowedDifferences("helloworld",
						"yellomarin", 3));
	}

	@Test
	public void testLargeCase() {
		String s1= "";
		String s2 = "";
		assertEquals(
				256,
				findLongestCommonSubstringWithAllowedDifferences(s1,s2,11));
	}
}
